mapper.select:
SELECT /* mapper.erd.entity.selectErdEntityList */ ENTT.ENTITY_ID AS ENTITY_ID , ENTT.ENTITY_NM , CASE WHEN NVL(ENTT.TRT_DT, CURRENT_TIMESTAMP) >= date_add(CURRENT_TIMESTAMP , interval #{SESSION_ENTITY_DISPLAY_DAYCNT} day) THEN CASE WHEN ENTT.DML_TCD = 'DML_TCD_C' THEN '+' WHEN ENTT.DML_TCD = 'DML_TCD_U' THEN '*' WHEN ENTT.DML_TCD IN( 'DML_TCD_I_D', 'DML_TCD_U_D') THEN '-' ELSE '' END ELSE '' END AS DML_TCD , ENTT.TABL_NM AS TABLE_NM , NVL((SELECT 'Y' FROM RELATION WHERE PROJECT_ID = ENTT.PROJECT_ID AND VERSN = ENTT.VERSN AND END_ENTITY_ID = ENTT.ENTITY_ID AND RELATION_TYPE = 'rel1toN' AND NON_IDEN_YN = 'N' LIMIT 1 ), 'N') AS IS_SUB_TABLE , '' AS LINE_COLOR , '' AS COLOR , '' AS BACKGROUND_COLOR , '' AS TABLE_BACKGROUND_COLOR , '' AS X , '' AS Y , nvl(WIDTH, '100') AS WIDTH , nvl(HEIGHT, '100') AS HEIGHT , ENTT.TABL_SCD , (SELECT CD_NM FROM CD WHERE CD_GRP = 'TABL_SCD' AND CD = ENTT.TABL_SCD) AS TABL_SCD_NM , (SELECT 'Y' FROM COLMN WHERE PROJECT_ID = ENTT.PROJECT_ID AND VERSN = ENTT.VERSN AND ENTITY_ID = ENTT.ENTITY_ID AND PK_YN = 'Y' LIMIT 1) AS HAS_PK , CASE WHEN ENTT.USE_YN = 'N' THEN 'N' ELSE 'Y' END AS USE_YN FROM ENTITY ENTT WHERE ENTT.PROJECT_ID = #{SESSION_PROJECT_ID} AND ENTT.VERSN = #{SESSION_VERSN} AND CASE WHEN ENTT.TABL_SCD = 'TABL_SCD_020' AND CASE WHEN ENTT.USE_YN = 'N' THEN 'N' ELSE 'Y' END = 'N' THEN 'N' WHEN CASE WHEN ENTT.USE_YN = 'N' THEN 'N' ELSE 'Y' END AND NVL(ENTT.TRT_DT, CURRENT_TIMESTAMP) < date_add(CURRENT_TIMESTAMP , interval #{SESSION_ENTITY_DISPLAY_DAYCNT} day) THEN 'N' ELSE 'Y' END = 'Y'
#{item}
ORDER BY ENTT.ENTITY_ID

SELECT /* mapper.erd.entity.selectEntityColumnTree */ ETT.ENTITY_ID AS ID , ETT.ENTITY_ID AS ENTITY_ID , 'TOP' AS UP_ENTITY_ID , ETT.ENTITY_ID AS COLMN_ID , ETT.ENTITY_NM AS COLMN_NM , NULL AS DOMAIN_ID , ETT.TABL_NM AS ATTR_NM , NULL AS DTYPE , NULL AS LEN1 , NULL AS LEN2 , NULL AS DML_TCD , NULL AS DML_TCD_NM , NULL AS DML_DT , NULL AS APLY_USR_UID , NULL AS APLY_DT , NULL AS TRT_USR_UID , NULL AS TRT_DT , ETT.TABL_SCD AS COLMN_SCD , (SELECT CD_NM FROM CD WHERE CD_GRP = 'TABL_SCD' AND CD = ETT.TABL_SCD) AS COLMN_SCD_NM , NULL AS NOTNULL_YN , NULL AS PK_YN , NULL AS FK_ENTITY_ID , NULL AS FK_COLMN_NM , NULL AS COLMN_DESC , NULL AS NUMB_MTH , ETT.VERSN , 1 AS ORD , -1 AS SEQ , 'N' AS SELECTED_YN , 'Y' AS EXPAND_YN , NULL AS COLOR , CONCAT('T-', CAST(ROW_NUMBER() OVER(ORDER BY ETT.ENTITY_NM) AS CHAR)) AS RNUM FROM ENTITY ETT WHERE ETT.PROJECT_ID = #{SESSION_PROJECT_ID} AND ETT.VERSN = #{SESSION_VERSN} AND CASE WHEN ETT.USE_YN = 'N' THEN 'N' ELSE 'Y' END = 'Y'
AND ETT.TABL_SCD = #{TABL_SCD}
AND EXISTS ( SELECT 'Y' FROM COLMN COL WHERE PROJECT_ID = ETT.PROJECT_ID AND ENTITY_ID = ETT.ENTITY_ID AND VERSN = ETT.VERSN AND COL.COLMN_SCD = #{COLMN_SCD} )
AND ( (
UPPER(REPLACE(#{ITEM},' ',''))
ETT.ENTITY_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
) OR (
UPPER(REPLACE(#{ITEM},' ',''))
ETT.TABL_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
)
)
AND
OR
EXISTS ( SELECT 'Y' FROM COLMN COL WHERE PROJECT_ID = ETT.PROJECT_ID AND ENTITY_ID = ETT.ENTITY_ID AND VERSN = ETT.VERSN AND (
UPPER(REPLACE(#{ITEM},' ',''))
COL.COLMN_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
) OR (
UPPER(REPLACE(#{ITEM},' ',''))
COL.ATTR_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
) )
)
UNION ALL SELECT COL.COLMN_ID AS ID , COL.ENTITY_ID , COL.ENTITY_ID AS UP_ENTITY_ID , COL.COLMN_ID , COL.COLMN_NM , COL.DOMAIN_ID , COL.ATTR_NM , CONCAT(COL.DTYPE , CASE WHEN UPPER(DTYPE) IN ('DATE', 'DATETIME') THEN '' ELSE CONCAT('(' , CASE WHEN LEN2 IS NULL THEN LEN1 ELSE CONCAT(LEN1,',',LEN2) END , ')') END ) AS DTYPE , COL.LEN1 , COL.LEN2 , COL.DML_TCD , (SELECT CD_NM FROM CD WHERE CD_GRP = 'DML_TCD' AND CD = COL.DML_TCD) AS DML_TCD_NM , COL.DML_DT , COL.APLY_USR_UID , COL.APLY_DT , COL.TRT_USR_UID , COL.TRT_DT , COL.COLMN_SCD , (SELECT CD_NM FROM CD WHERE CD_GRP = 'TABL_SCD' AND CD = COL.COLMN_SCD) AS COLMN_SCD_NM , COL.NOTNULL_YN , COL.PK_YN , COL.FK_ENTITY_ID , COL.FK_COLMN_NM , COL.COLMN_DESC , COL.NUMB_MTH , COL.VERSN , 2 AS ORD , COL.SEQ , 'N' AS SELECTED_YN , 'N' AS EXPAND_YN , COL.COLOR , CAST(ROW_NUMBER() OVER(PARTITION BY COL.ENTITY_ID ORDER BY SEQ, COLMN_NM) AS CHAR) AS RNUM FROM COLMN COL , ENTITY ETT WHERE COL.PROJECT_ID = #{SESSION_PROJECT_ID} AND COL.VERSN = #{SESSION_VERSN} AND ETT.PROJECT_ID = COL.PROJECT_ID AND ETT.ENTITY_ID = COL.ENTITY_ID AND ETT.VERSN = COL.VERSN AND CASE WHEN COL.USE_YN = 'N' THEN 'N' ELSE 'Y' END = 'Y'
AND ETT.TABL_SCD = #{TABL_SCD}
AND COL.COLMN_SCD = #{COLMN_SCD}
AND ( (
UPPER(REPLACE(#{ITEM},' ',''))
ETT.ENTITY_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
) OR (
UPPER(REPLACE(#{ITEM},' ',''))
ETT.TABL_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
) )
AND ( (
UPPER(REPLACE(#{ITEM},' ',''))
COL.COLMN_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
) OR (
UPPER(REPLACE(#{ITEM},' ',''))
COL.ATTR_NM LIKE CONCAT('%', UPPER(REPLACE(#{ITEM},' ','')), '%')
) )
ORDER BY ORD, CASE WHEN PK_YN = 'Y' THEN 1 ELSE 2 END, SEQ, COLMN_NM

mapper.insert:
INSERT /* mapper.erd.entity.saveEntity */ INTO ENTITY ( PROJECT_ID , VERSN , ENTITY_ID , ENTITY_NM , TABL_NM , TABL_SCD
, DML_TCD , DML_DT , TABL_DESC , APLY_USR_UID , APLY_DT , USE_YN
, TRT_USR_UID , TRT_DT
, FRST_INS_DT , FRST_INS_USR_UID , LAST_UPD_DT , LAST_UPD_USR_UID
, LOG_ID , CHG_CTNT
) VALUES ( #{SESSION_PROJECT_ID} , #{SESSION_VERSN} , #{ENTITY_ID} , #{ENTITY_NM} , #{TABL_NM} , #{TABL_SCD}
, 'DML_TCD_C' , CURRENT_TIMESTAMP , #{TABL_DESC}
, CASE WHEN #{TABL_SCD} IN ('TABL_SCD_010') THEN #{SESSION_USR_UID} ELSE NULL END /* APLY_USR_UID */ , CASE WHEN #{TABL_SCD} IN ('TABL_SCD_010') THEN CURRENT_TIMESTAMP ELSE NULL END /* APLY_DT */ , NVL(#{USE_YN}, 'Y') /* USE_YN */
, CASE WHEN #{TABL_SCD} IN ('TABL_SCD_040', 'TABL_SCD_050', 'TABL_SCD_060', 'TABL_SCD_070') THEN #{SESSION_USR_UID} ELSE NULL END /* TRT_USR_UID */ , CASE WHEN #{TABL_SCD} IN ('TABL_SCD_040', 'TABL_SCD_050', 'TABL_SCD_060', 'TABL_SCD_070') THEN CURRENT_TIMESTAMP ELSE NULL END /* TRT_DT */
, CURRENT_TIMESTAMP , #{SESSION_USR_UID} , CURRENT_TIMESTAMP , #{SESSION_USR_UID}
, #{LOG_ID} , CONCAT('테이블 "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"이 "', NVL((SELECT CD_NM FROM CD WHERE CD = #{TABL_SCD}), '반영"') ,'되었습니다.') ) ON DUPLICATE KEY UPDATE LOG_ID = #{LOG_ID} , CHG_CTNT = CASE WHEN ENTITY_NM = #{ENTITY_NM} AND TABL_NM = #{TABL_NM} AND TABL_SCD = #{TABL_SCD} THEN NULL WHEN USE_YN = 'Y' AND NVL(#{USE_YN}, 'Y') = 'N' THEN CONCAT('테이블 "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"이 삭제 되었습니다.') WHEN USE_YN = 'N' AND NVL(#{USE_YN}, 'Y') = 'Y' THEN CASE WHEN ENTITY_NM = #{ENTITY_NM} AND TABL_NM = #{TABL_NM} AND TABL_SCD != #{TABL_SCD} THEN CONCAT('테이블 "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"이 삭제취소되어 "', NVL((SELECT CD_NM FROM CD WHERE CD = #{TABL_SCD}), '반영"') ,'되었습니다.') WHEN (ENTITY_NM != #{ENTITY_NM} OR TABL_NM != #{TABL_NM}) AND TABL_SCD = #{TABL_SCD} THEN CONCAT('테이블 "', ENTITY_NM, '(',TABL_NM, ')"이 삭제취소되어 -> "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"로 변경 되었습니다.') WHEN (ENTITY_NM != #{ENTITY_NM} OR TABL_NM != #{TABL_NM}) AND TABL_SCD != #{TABL_SCD} THEN CONCAT('테이블 "', ENTITY_NM, '(',TABL_NM, ')"이 삭제취소되어 -> "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"로 변경되어 "', NVL((SELECT CD_NM FROM CD WHERE CD = #{TABL_SCD}), '반영"') ,'되었습니다.') ELSE NULL END WHEN ENTITY_NM = #{ENTITY_NM} AND TABL_NM = #{TABL_NM} AND TABL_SCD != #{TABL_SCD} THEN CONCAT('테이블 "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"이 "', NVL((SELECT CD_NM FROM CD WHERE CD = #{TABL_SCD}), '반영"') ,'되었습니다.') WHEN (ENTITY_NM != #{ENTITY_NM} OR TABL_NM != #{TABL_NM}) AND TABL_SCD = #{TABL_SCD} THEN CONCAT('테이블 "', ENTITY_NM, '(',TABL_NM, ')"이 -> "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"로 변경 되었습니다.') WHEN (ENTITY_NM != #{ENTITY_NM} OR TABL_NM != #{TABL_NM}) AND TABL_SCD != #{TABL_SCD} THEN CONCAT('테이블 "', ENTITY_NM, '(',TABL_NM, ')"이 -> "', #{TABL_NM}, '(', #{ENTITY_NM}, ')"로 변경되어 "', NVL((SELECT CD_NM FROM CD WHERE CD = #{TABL_SCD}), '반영"') ,'되었습니다.') ELSE NULL END
, ENTITY_NM = #{ENTITY_NM} , TABL_NM = #{TABL_NM} , TABL_SCD = #{TABL_SCD}
, DML_TCD = CASE WHEN DML_TCD = 'DML_TCD_C' AND #{TABL_SCD} IN ('TABL_SCD_040', 'TABL_SCD_050', 'TABL_SCD_060', 'TABL_SCD_070') THEN 'DML_TCD_U' ELSE 'DML_TCD_C' END , DML_DT = CASE WHEN DML_TCD = 'DML_TCD_C' AND #{TABL_SCD} IN ('TABL_SCD_040', 'TABL_SCD_050', 'TABL_SCD_060', 'TABL_SCD_070') THEN CURRENT_TIMESTAMP ELSE DML_DT END , TABL_DESC = #{TABL_DESC} , APLY_USR_UID = CASE WHEN #{TABL_SCD} IN ('TABL_SCD_010') THEN #{SESSION_USR_UID} ELSE NVL(APLY_USR_UID, #{SESSION_USR_UID}) END , APLY_DT = CASE WHEN #{TABL_SCD} IN ('TABL_SCD_010') THEN CURRENT_TIMESTAMP ELSE NVL(APLY_DT, CURRENT_TIMESTAMP) END , USE_YN = NVL(#{USE_YN}, 'Y')
, LAST_UPD_DT = CURRENT_TIMESTAMP , LAST_UPD_USR_UID =#{SESSION_USR_UID}

INSERT /* mapper.erd.entity.saveSubjectEntity */
  INTO SUBJECT_ENTITY (
         PROJECT_ID 
       , SUBJECT_ID 
       , VERSN 
       , ENTITY_ID
       , X
       , Y
       , COLOR 
       , LINE_COLOR 
       , NM_BG_COLOR
       , BG_COLOR 
       , WIDTH 
       , HEIGHT 
       , USE_YN 
       
       , FRST_INS_DT 
       , FRST_INS_USR_UID
       , LAST_UPD_DT 
       , LAST_UPD_USR_UID
  
) VALUES (
     #{SESSION_PROJECT_ID}    
   , #{SUBJECT_ID}      
   , #{SESSION_VERSN}
   , #{ENTITY_ID}
   , #{X}
   , #{Y}
   , #{COLOR}
   , #{LINE_COLOR}
   , #{NM_BG_COLOR}
   , #{BG_COLOR}
   , #{WIDTH}
   , #{HEIGHT}
   , CASE WHEN #{USE_YN}      IS NULL THEN 'Y'         ELSE #{USE_YN}      END
   
   , CURRENT_TIMESTAMP     
   , #{SESSION_USR_UID}
   , CURRENT_TIMESTAMP   
   , #{SESSION_USR_UID}
)
ON DUPLICATE KEY UPDATE
      X           = CASE WHEN #{X}           IS NULL THEN X           ELSE #{X}           END 
    , Y           = CASE WHEN #{Y}           IS NULL THEN Y           ELSE #{Y}           END 
    , COLOR       = CASE WHEN #{COLOR}       IS NULL THEN COLOR       ELSE #{COLOR}       END 
    , LINE_COLOR  = CASE WHEN #{LINE_COLOR}  IS NULL THEN LINE_COLOR  ELSE #{LINE_COLOR}  END 
    , NM_BG_COLOR = CASE WHEN #{NM_BG_COLOR} IS NULL THEN NM_BG_COLOR ELSE #{NM_BG_COLOR} END 
    , BG_COLOR    = CASE WHEN #{BG_COLOR}    IS NULL THEN BG_COLOR    ELSE #{BG_COLOR}    END 
    , WIDTH       = CASE WHEN #{WIDTH}       IS NULL THEN WIDTH       ELSE #{WIDTH}       END 
    , HEIGHT      = CASE WHEN #{HEIGHT}      IS NULL THEN HEIGHT      ELSE #{HEIGHT}      END 
    , USE_YN      = CASE WHEN #{USE_YN}      IS NULL THEN 'Y'         ELSE #{USE_YN}      END
   
   , LAST_UPD_DT     = CURRENT_TIMESTAMP 
   , LAST_UPD_USR_UID =#{SESSION_USR_UID}

mapper.update:
        UPDATE /* mapper.erd.entity.updateEntityUseYn */
               ENTITY 
           SET 
               USE_YN      = #{USE_YN} 
             , DML_TCD     = CASE WHEN #{USE_YN} = 'Y' THEN 
                                       CASE WHEN DML_TCD = 'DML_TCD_C_D' THEN 'DML_TCD_C'
                                            ELSE 'DML_TCD_U' END
                                  ELSE CASE WHEN DML_TCD = 'DML_TCD_C' THEN 'DML_TCD_C_D'
                                            ELSE 'DML_TCD_U_D' END
                                  END
             
             , DML_DT      = CURRENT_TIMESTAMP
             , DML_USR_UID = #{SESSION_USR_UID} 

             , APLY_USR_UID = CASE WHEN #{TABL_SCD} = 'TABL_SCD_010' THEN #{SESSION_USR_UID} ELSE APLY_USR_UID END
             , APLY_DT      = CASE WHEN #{TABL_SCD} = 'TABL_SCD_010' THEN CURRENT_TIMESTAMP ELSE APLY_DT END

             , TRT_USR_UID  = #{SESSION_USR_UID}
             , TRT_DT       = CURRENT_TIMESTAMP
         WHERE 1 = 1
           AND PROJECT_ID = #{SESSION_PROJECT_ID}
           AND VERSN      = #{SESSION_VERSN}
           AND ENTITY_ID  = #{ENTITY_ID}

mapper.delete:
UPDATE /* mapper.erd.entity.updateSubjectRelationOfEntityUseYn */
       SUBJECT_RELATION SR
   SET 
       USE_YN = #{USE_YN} 
     , LINE_PATH = CASE WHEN #{USE_YN} = 'N' THEN NULL ELSE LINE_PATH END
     , LAST_UPD_DT      = CURRENT_TIMESTAMP 
     , LAST_UPD_USR_UID = #{SESSION_USR_UID}
 WHERE PROJECT_ID = #{SESSION_PROJECT_ID}
   AND VERSN      = #{SESSION_VERSN}
   AND SUBJECT_ID = #{SUBJECT_ID}   
   AND RELATION_ID IN (SELECT RELATION_ID
                         FROM RELATION
                        WHERE PROJECT_ID = SR.PROJECT_ID 
                          AND ( START_ENTITY_ID  = #{ENTITY_ID} 
                                OR END_ENTITY_ID  = #{ENTITY_ID} 
                               ) 
                       )

