         OR END_ENTITY_ID  = #{ENTITY_ID} 
        )

mapper.update:
        UPDATE /* mapper.erd.entity.updateSubjectEntityUseYn */
               SUBJECT_ENTITY
           SET 
               USE_YN      = #{USE_YN}
             , X           = CASE WHEN #{X} IS NULL OR #{X} = '' THEN X ELSE #{X} END 
             , Y           = CASE WHEN #{Y} IS NULL OR #{Y} = '' THEN Y ELSE #{Y} END 
             , LAST_UPD_DT     = CURRENT_TIMESTAMP 
             , LAST_UPD_USR_UID =#{SESSION_USR_UID}
         WHERE 1 = 1
           AND PROJECT_ID = #{SESSION_PROJECT_ID}
           AND SUBJECT_ID = #{SUBJECT_ID}        
           AND VERSN      = #{SESSION_VERSN}     
           AND ENTITY_ID  = #{ENTITY_ID}

UPDATE /* mapper.erd.entity.updateEntityScd */
       ENTITY 
   SET 
       LOG_ID      = #{LOG_ID}
     , CHG_CTNT    = CASE WHEN TABL_SCD  != #{TABL_SCD} THEN
                               CONCAT('테이블 "', TABL_NM, '(', ENTITY_NM, ')"이 "', NVL((SELECT CD_NM FROM CD WHERE CD = #{TABL_SCD}), '반영"') ,'되었습니다.')
                          ELSE NULL END
     , TABL_SCD    = #{TABL_SCD}
     , TRT_DT      = CURRENT_TIMESTAMP
     , TRT_USR_UID = #{SESSION_USR_UID} 
     , DML_DT      = CURRENT_TIMESTAMP
     , DML_USR_UID = #{SESSION_USR_UID} 
 WHERE 1 = 1
   AND PROJECT_ID = #{SESSION_PROJECT_ID}
   AND VERSN      = #{SESSION_VERSN}
   AND ENTITY_ID  = #{ENTITY_ID}

        UPDATE  /* mapper.erd.entity.updateFKColumnUseYn */
               COLMN COL
           SET 
               USE_YN      = #{USE_YN} 
             , DML_TCD     = CASE WHEN #{USE_YN} = 'Y' THEN 'DML_TCD_U' ELSE 'DML_TCD_D' END
             , DML_DT      = CURRENT_TIMESTAMP

             , TRT_USR_UID  = #{SESSION_USR_UID} 
             , TRT_DT       = CURRENT_TIMESTAMP
         WHERE COL.PROJECT_ID = #{SESSION_PROJECT_ID}
           AND COL.VERSN      = #{SESSION_VERSN}
           AND (COL.FK_ENTITY_ID, COL.FK_COLMN_ID) 
               IN (SELECT ENTITY_ID, COLMN_ID
                     FROM COLMN
                    WHERE PROJECT_ID = COL.PROJECT_ID
                      AND VERSN      = COL.VERSN
                      AND ENTITY_ID  = #{ENTITY_ID}
                      AND PK_YN      = 'Y'
                   )

UPDATE /* mapper.erd.entity.updateTableNameByWord */ ENTITY ENTT JOIN (SELECT ENTT.PROJECT_ID, ENTT.VERSN, ENTT.ENTITY_ID , REPLACE(GROUP_CONCAT(ENTT.WORD ORDER BY ORD), ',', '_') AS WORD, REPLACE(GROUP_CONCAT(NVL(WORD.ABBR, ENTT.WORD) ORDER BY ORD), ',', '_') AS ENTITY_NM FROM (SELECT ENTT.PROJECT_ID, ENTT.VERSN, ENTT.ENTITY_ID, ENTT.TABL_NM, LENGTH(ENTT.TABL_NM) - LENGTH(REPLACE(ENTT.TABL_NM,' ', '')) - RN + 2 AS ORD , SUBSTRING_INDEX(SUBSTRING_INDEX(ENTT.TABL_NM, ' ', -RN), ' ', 1) WORD FROM ENTITY ENTT , (SELECT 1 RN UNION ALL SELECT 2 RN UNION ALL SELECT 3 RN UNION ALL SELECT 4 RN UNION ALL SELECT 5 RN UNION ALL SELECT 6 RN UNION ALL SELECT 7 RN UNION ALL SELECT 8 RN UNION ALL SELECT 9 RN UNION ALL SELECT 10 RN ) RN WHERE ENTT.PROJECT_ID = #{SESSION_PROJECT_ID} AND ENTT.VERSN = #{SESSION_VERSN}
AND ENTT.ENTITY_ID = #{ENTITY_ID}
AND LENGTH(ENTT.TABL_NM)-LENGTH(REPLACE(ENTT.TABL_NM,' ', '')) >= RN.RN-1 ) ENTT LEFT OUTER JOIN WORD WORD ON ENTT.PROJECT_ID = WORD.PROJECT_ID AND ENTT.WORD = WORD.WORD GROUP BY ENTT.PROJECT_ID, ENTT.VERSN, ENTT.ENTITY_ID) WORD ON ENTT.PROJECT_ID = WORD.PROJECT_ID AND ENTT.VERSN = WORD.VERSN AND ENTT.ENTITY_ID = WORD.ENTITY_ID AND ENTT.ENTITY_NM != WORD.ENTITY_NM
SET LOG_ID = #{LOG_ID} , CHG_CTNT = CONCAT('테이블 "', TABL_NM,'(', ENTT.ENTITY_NM, ')"이 -> "', TABL_NM, '(', WORD.ENTITY_NM ,')"로 변경 되었습니다.') , ENTT.ENTITY_NM = WORD.ENTITY_NM , ENTT.DML_TCD = CASE WHEN ENTT.DML_TCD IN ('DML_TCD_I_D', 'DML_TCD_U_D') THEN ENTT.DML_TCD ELSE 'DML_TCD_U' END , ENTT.DML_DT = CASE WHEN ENTT.DML_TCD IN ('DML_TCD_I_D', 'DML_TCD_U_D') THEN ENTT.DML_DT ELSE CURRENT_TIMESTAMP END , LAST_UPD_DT = CURRENT_TIMESTAMP , LAST_UPD_USR_UID = #{SESSION_USR_NM} WHERE 1 = 1 AND ENTT.PROJECT_ID = #{SESSION_PROJECT_ID} AND ENTT.VERSN = #{SESSION_VERSN}
AND ENTT.ENTITY_ID = #{ENTITY_ID}

