mapper.select:
    SELECT /* mapper.erd.relation.selectRelationByStartEndEntity */
           REL.RELATION_ID
      FROM RELATION REL
     WHERE REL.PROJECT_ID      = #{SESSION_PROJECT_ID}
       AND REL.VERSN           = #{SESSION_VERSN}
       AND REL.START_ENTITY_ID = #{START_ENTITY_ID}
       AND REL.END_ENTITY_ID   = #{END_ENTITY_ID}

SELECT /* mapper.erd.relation.selectSubjectRelationErdList */ SUBJ_REL.SUBJECT_ID , SUBJ_REL.RELATION_ID , CONCAT(REL.START_ENTITY_ID, '_', REL.END_ENTITY_ID) AS RELATION , REL.RELATION_NM AS RELATION_NAME , REL.RELATION_TYPE AS RELATION_TYPE , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.LINE_COLOR WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.LINE_COLOR ELSE NVL(REL_USR.LINE_COLOR, SUBJ_REL.LINE_COLOR) END AS COLOR , CASE WHEN REL.START_ENTITY_ID = REL.END_ENTITY_ID THEN 'Y' ELSE NULL END AS RECURSIVE_YN , REL.START_ENTITY_ID , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.START_POSITION WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.START_POSITION ELSE NVL(REL_USR.START_POSITION, SUBJ_REL.START_POSITION) END AS START_POSITION , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.START_X WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.START_X ELSE NVL(REL_USR.START_X, SUBJ_REL.START_X) END AS START_X , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.START_Y WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.START_Y ELSE NVL(REL_USR.START_Y, SUBJ_REL.START_Y) END AS START_Y , REL.END_ENTITY_ID , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.END_POSITION WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.END_POSITION ELSE NVL(REL_USR.END_POSITION, SUBJ_REL.END_POSITION) END AS END_POSITION , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.END_X WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.END_X ELSE NVL(REL_USR.END_X, SUBJ_REL.END_X) END AS END_X , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.END_Y WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.END_Y ELSE NVL(REL_USR.END_Y, SUBJ_REL.END_Y) END AS END_Y , REL.NON_IDEN_YN AS NON_IDEN_YN , CASE WHEN SUBJ.EDIT_USR_UID = 'GUEST' THEN SUBJ_REL.LINE_PATH WHEN SUBJ.EDIT_USR_UID = #{SESSION_USR_UID} OR USR_PJT.AUTH_CD = 'MANAGER' OR USR_PJT.BASE_YN = 'Y' THEN SUBJ_REL.LINE_PATH ELSE NVL(REL_USR.LINE_PATH, SUBJ_REL.LINE_PATH) END AS PATHS , CASE WHEN SUBJ_REL.USE_YN = 'N' THEN 'Y' WHEN REL.USE_YN = 'N' THEN 'Y' ELSE 'N' END AS DEL_YN FROM SUBJECT_RELATION SUBJ_REL LEFT OUTER JOIN SUBJECT_RELATION_USR REL_USR ON SUBJ_REL.PROJECT_ID = REL_USR.PROJECT_ID AND SUBJ_REL.VERSN = REL_USR.VERSN AND SUBJ_REL.SUBJECT_ID = REL_USR.SUBJECT_ID AND SUBJ_REL.RELATION_ID = REL_USR.RELATION_ID AND #{SESSION_USR_UID} = REL_USR.USR_ID LEFT OUTER JOIN USR_PROJECT USR_PJT ON SUBJ_REL.PROJECT_ID = USR_PJT.PROJECT_ID AND #{SESSION_USR_UID} = USR_PJT.USR_UID , RELATION REL , SUBJECT SUBJ WHERE 1 = 1 AND SUBJ_REL.PROJECT_ID = REL.PROJECT_ID AND SUBJ_REL.VERSN = REL.VERSN AND SUBJ_REL.RELATION_ID = REL.RELATION_ID
AND SUBJ_REL.PROJECT_ID = SUBJ.PROJECT_ID AND SUBJ_REL.VERSN = SUBJ.VERSN AND SUBJ_REL.SUBJECT_ID = SUBJ.SUBJECT_ID
AND SUBJ_REL.PROJECT_ID = #{SESSION_PROJECT_ID}

mapper.insert:
        INSERT /* mapper.erd.relation.saveRelation */
          INTO RELATION (
                    PROJECT_ID 
                  , VERSN
                  , RELATION_ID 
                  , START_ENTITY_ID 
                  , END_ENTITY_ID 
                  , RELATION_NM 
                  , RELATION_TYPE 
                  , LINE_COLOR 
                  , NON_IDEN_YN 
                  , FRST_INS_DT 
                  , FRST_INS_USR_UID 
                  , LAST_UPD_DT 
                  , LAST_UPD_USR_UID 
           ) VALUES (
                    #{SESSION_PROJECT_ID} 
                  , #{SESSION_VERSN}
                  , #{RELATION_ID}
                  , #{START_ENTITY_ID} 
                  , #{END_ENTITY_ID} 
                  , #{RELATION_NM} 
                  , #{RELATION_TYPE} 
                  , #{LINE_COLOR}
                  , #{NON_IDEN_YN} 
                  , CURRENT_TIMESTAMP     
                  , #{SESSION_USR_NM}
                  , CURRENT_TIMESTAMP   
                  , #{SESSION_USR_NM}
           )
        ON DUPLICATE KEY UPDATE

             RELATION_NM     = CASE WHEN #{RELATION_NM}   IS NULL THEN RELATION_NM   ELSE #{RELATION_NM}   END   
           , RELATION_TYPE   = CASE WHEN #{RELATION_TYPE} IS NULL THEN RELATION_TYPE ELSE #{RELATION_TYPE} END  
           , LINE_COLOR      = CASE WHEN #{LINE_COLOR}    IS NULL THEN LINE_COLOR    ELSE #{LINE_COLOR}    END     
           , NON_IDEN_YN     = CASE WHEN #{NON_IDEN_YN}   IS NULL THEN NON_IDEN_YN   ELSE #{NON_IDEN_YN}   END    
           
           , LAST_UPD_DT     = CURRENT_TIMESTAMP 
           , LAST_UPD_USR_UID =#{SESSION_USR_NM}

INSERT /* mapper.erd.relation.saveRelationAttr */
  INTO RELATION_ATTR (
            PROJECT_ID 
          , VERSN
          , RELATION_ID 
          , ATTR 
          , VAL 
          , FRST_INS_DT 
          , FRST_INS_USR_UID 
          , LAST_UPD_DT 
          , LAST_UPD_USR_UID 
   ) VALUES (
            #{SESSION_PROJECT_ID} 
          , #{SESSION_VERSN}
          , #{RELATION_ID}
          , #{ATTR} 
          , #{VAL} 
          , CURRENT_TIMESTAMP     
          , #{SESSION_USR_NM}
          , CURRENT_TIMESTAMP   
          , #{SESSION_USR_NM}
   )
ON DUPLICATE KEY UPDATE
     ATTR = #{ATTR}
   , VAL   = #{VAL}  
   
   , LAST_UPD_DT     = CURRENT_TIMESTAMP 
   , LAST_UPD_USR_UID =#{SESSION_USR_NM}

        INSERT /* mapper.erd.relation.saveSubjectRelation */
          INTO SUBJECT_RELATION (
                  PROJECT_ID 
                , VERSN  
                , SUBJECT_ID 
                , RELATION_ID 
                , START_POSITION 
                , START_Y 
                , START_X 
                
                , END_POSITION 
                , END_X 
                , END_Y 
                
                , LINE_PATH
                , LINE_COLOR
                
                , FRST_INS_DT 
                , FRST_INS_USR_UID 
                , LAST_UPD_DT 
                , LAST_UPD_USR_UID 

           ) VALUES (
                  #{SESSION_PROJECT_ID} 
                , #{SESSION_VERSN}
                , #{SUBJECT_ID} 
                , #{RELATION_ID} 
                 
                , #{START_POSITION} 
                , #{START_Y} 
                , #{START_X} 
                  
                , #{END_POSITION} 
                , #{END_X}
                , #{END_Y} 
                
                , #{LINE_PATH}
                , #{LINE_COLOR}
                
                , CURRENT_TIMESTAMP     
                , #{SESSION_USR_NM}
                , CURRENT_TIMESTAMP   
                , #{SESSION_USR_NM}
           )
        ON DUPLICATE KEY UPDATE
              START_POSITION = CASE WHEN #{START_POSITION} IS NULL THEN START_POSITION ELSE #{START_POSITION} END
            , START_Y        = CASE WHEN #{START_Y}        IS NULL THEN START_Y        ELSE #{START_Y}        END
            , START_X        = CASE WHEN #{START_X}        IS NULL THEN START_X        ELSE #{START_X}        END

            , END_POSITION   = CASE WHEN #{END_POSITION}   IS NULL THEN END_POSITION   ELSE #{END_POSITION}   END
            , END_X          = CASE WHEN #{END_X}          IS NULL THEN END_X          ELSE #{END_X}          END
            , END_Y          = CASE WHEN #{END_Y}          IS NULL THEN END_Y          ELSE #{END_Y}          END

            , LINE_PATH      = CASE WHEN #{LINE_PATH}      IS NULL THEN LINE_PATH      ELSE #{LINE_PATH}      END
            , LINE_COLOR     = CASE WHEN #{LINE_COLOR}     IS NULL THEN LINE_COLOR     ELSE #{LINE_COLOR}      END
           , LAST_UPD_DT     = CURRENT_TIMESTAMP 
           , LAST_UPD_USR_UID =#{SESSION_USR_NM}

        INSERT /* mapper.erd.relation.saveSubjectRelationUsr */
          INTO SUBJECT_RELATION_USR (
                  PROJECT_ID 
                , VERSN  
                , SUBJECT_ID 
                , RELATION_ID 
                , USR_ID 
                , START_POSITION 
                , START_Y 
                , START_X 
                
                , END_POSITION 
                , END_X 
                , END_Y 
                
                , LINE_PATH
                , LINE_COLOR
                
                , FRST_INS_DT 
                , FRST_INS_USR_UID 
                , LAST_UPD_DT 
                , LAST_UPD_USR_UID 

           ) VALUES (
                  #{SESSION_PROJECT_ID} 
                , #{SESSION_VERSN}
                , #{SUBJECT_ID} 
                , #{RELATION_ID} 
                , NVL(#{USR_ID}, #{SESSION_USR_UID})
                 
                , #{START_POSITION} 
                , #{START_Y} 
                , #{START_X} 
                  
                , #{END_POSITION} 
                , #{END_X}
                , #{END_Y} 
                
                , #{LINE_PATH}
                , #{LINE_COLOR}
                
                , CURRENT_TIMESTAMP     
                , #{SESSION_USR_NM}
                , CURRENT_TIMESTAMP   
                , #{SESSION_USR_NM}
           )
        ON DUPLICATE KEY UPDATE
              START_POSITION = CASE WHEN #{START_POSITION} IS NULL THEN START_POSITION ELSE #{START_POSITION} END
            , START_Y        = CASE WHEN #{START_Y}        IS NULL THEN START_Y        ELSE #{START_Y}        END
            , START_X        = CASE WHEN #{START_X}        IS NULL THEN START_X        ELSE #{START_X}        END

            , END_POSITION   = CASE WHEN #{END_POSITION}   IS NULL THEN END_POSITION   ELSE #{END_POSITION}   END
            , END_X          = CASE WHEN #{END_X}          IS NULL THEN END_X          ELSE #{END_X}          END
            , END_Y          = CASE WHEN #{END_Y}          IS NULL THEN END_Y          ELSE #{END_Y}          END

            , LINE_PATH      = CASE WHEN #{LINE_PATH}      IS NULL THEN LINE_PATH      ELSE #{LINE_PATH}      END
            , LINE_COLOR     = CASE WHEN #{LINE_COLOR}     IS NULL THEN LINE_COLOR     ELSE #{LINE_COLOR}     END
            
           , LAST_UPD_DT     = CURRENT_TIMESTAMP 
           , LAST_UPD_USR_UID =#{SESSION_USR_NM}

mapper.delete:
DELETE /* mapper.erd.relation.deleteRelation */
  FROM RELATION 
 WHERE 1 = 1
   AND PROJECT_ID  = #{SESSION_PROJECT_ID} 
   AND VERSN       = #{SESSION_VERSN}
   AND RELATION_ID = #{RELATION_ID}

DELETE /* mapper.erd.relation.deleteRelationAttr */
  FROM RELATION_ATTR 
 WHERE 1 = 1
   AND PROJECT_ID  = #{SESSION_PROJECT_ID} 
   AND VERSN       = #{SESSION_VERSN}
   AND RELATION_ID = #{RELATION_ID}

DELETE /* mapper.erd.relation.deleteSubjectRelation */
  FROM SUBJECT_RELATION 
 WHERE 1 = 1
   AND PROJECT_ID  = #{SESSION_PROJECT_ID} 
   AND VERSN       = #{SESSION_VERSN}
   AND SUBJECT_ID  = #{SUBJECT_ID} 
   AND RELATION_ID = #{RELATION_ID}

mapper.update:
UPDATE /* mapper.erd.relation.updateRelationPath */
       SUBJECT_RELATION SUBJ_REL
   SET 
       LINE_PATH = #{PATHS} 
     , LAST_UPD_DT     = CURRENT_TIMESTAMP 
     , LAST_UPD_USR_UID =#{SESSION_USR_NM}
 WHERE 1 = 1
   AND PROJECT_ID  = #{SESSION_PROJECT_ID} 
   AND VERSN       = #{SESSION_VERSN}
   AND SUBJECT_ID  = #{SUBJECT_ID} 
   AND RELATION_ID = #{RELATION_ID}

UPDATE /* mapper.erd.relation.updateRelationPathUsr */
       SUBJECT_RELATION_USR SUBJ_REL
   SET 
       LINE_PATH = #{PATHS} 
     , LAST_UPD_DT     = CURRENT_TIMESTAMP 
     , LAST_UPD_USR_UID =#{SESSION_USR_NM}
 WHERE 1 = 1
   AND PROJECT_ID  = #{SESSION_PROJECT_ID} 
   AND VERSN       = #{SESSION_VERSN}
   AND SUBJECT_ID  = #{SUBJECT_ID} 
   AND RELATION_ID = #{RELATION_ID} 
   AND USR_ID      = #{SESSION_USR_UID}
