mapper.select:
SELECT COUNT(*) CNT
  FROM USR
 WHERE LOGIN_ID = #{LOGIN_ID}

mapper.insert:
INSERT INTO USR (
     USR_UID      
   , COMPANY_ID
   , USR_NM
   , LOGIN_ID
   , PWD
   , PROJECT_ID
   , FRST_INS_DT     
   , FRST_INS_USR_UID
   , LAST_UPD_DT     
   , LAST_UPD_USR_UID
) VALUES (
     #{USR_UID} 
   , NVL(#{SESSION_COMPANY_ID}, 'DEFAULT')
   , #{USR_NM}
   , #{LOGIN_ID}
   , #{PWD}
   , #{PROJECT_ID}
   , CURRENT_TIMESTAMP     
   , #{SESSION_USR_NM}
   , CURRENT_TIMESTAMP   
   , #{SESSION_USR_NM}
)
ON DUPLICATE KEY UPDATE
     PWD = #{PWD}
   , COMPANY_ID = NVL(#{SESSION_COMPANY_ID}, 'DEFAULT')
   , USR_NM =  #{USR_NM}
   , PROJECT_ID =  #{PROJECT_ID}
   , LAST_UPD_DT  =   CURRENT_TIMESTAMP 
   , LAST_UPD_USR_UID =#{SESSION_USR_NM}
