mapper.insert:
INSERT /* mapper.erd.word.insertWord */ INTO WORD ( PROJECT_ID , WORD_ID , WORD , FRST_INS_DT , FRST_INS_USR_UID , LAST_UPD_DT , LAST_UPD_USR_UID ) SELECT #{SESSION_PROJECT_ID} AS PROJECT_ID , UUID() AS WORD_ID , A.WORD AS WORD , CURRENT_TIMESTAMP , #{SESSION_USR_UID} , CURRENT_TIMESTAMP , #{SESSION_USR_UID} FROM ( SELECT NULL AS WORD
UNION ALL SELECT #{item} AS WORD
) A WHERE A.WORD IS NOT NULL AND A.WORD REGEXP '^[A-Za-z[:space:]]+$' = 0 AND NOT EXISTS (SELECT 'Y' FROM WORD WHERE PROJECT_ID = #{SESSION_PROJECT_ID} AND WORD = A.WORD )

mapper.select:
SELECT /* mapper.erd.word.selectWordList */ WORD_ID, WORD, ABBR, ENGLISH, WORD_DESC, 'Y' AS USE_YN , DATE_FORMAT(LAST_UPD_DT, '%Y-%m-%d %H:%i:%S') AS LAST_UPD_DT_FMT FROM WORD WHERE PROJECT_ID = #{SESSION_PROJECT_ID}
AND
#{ITEM}
WORD LIKE CONCAT('%', #{ITEM}, '%')
ORDER BY WORD

mapper.update:
        UPDATE /* mapper.erd.word.updateWord */
               WORD 
           SET
              LOG_ID     = #{LOG_ID}
            , CHG_CTNT   = CASE WHEN ABBR != #{ABBR} 
                                     THEN CONCAT('단어 "', #{WORD}, '의 용어가 "', ABBR , '"에서 "', #{ABBR} ,'"로 변경되었습니다.')
                                ELSE NULL END
            , ABBR       = #{ABBR        }
            , ENGLISH    = #{ENGLISH     }
            , WORD_DESC  = #{WORD_DESC   }

            , LAST_UPD_DT     = CURRENT_TIMESTAMP
            , LAST_UPD_USR_UID= #{SESSION_USR_UID }
        WHERE PROJECT_ID = #{SESSION_PROJECT_ID}
          AND WORD_ID    = #{WORD_ID}

mapper.delete:
DELETE /* mapper.erd.word.deleteWord */
  FROM WORD
 WHERE PROJECT_ID  = #{SESSION_PROJECT_ID}
   AND WORD_ID     = #{WORD_ID       }
